package com.zlk.bigdemo.app.widget.selector.adapter;import android.content.Context;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.TextView;import com.zlk.bigdemo.R;import com.zlk.bigdemo.app.widget.selector.support.AlbumItem;import com.zlk.bigdemo.app.widget.selector.utils.AlbumManager;import  com.zlk.bigdemo.freeza.image.MagicImageView;import  com.zlk.bigdemo.freeza.widget.adapter.ViewHolder;import java.util.List;public class AlbumAdapter extends BaseAdapter {	private Context context;	protected List<AlbumItem> menuList;	public AlbumAdapter(Context context, List<AlbumItem> menuList) {		this.context = context;		this.menuList = menuList;	}	@Override	public View getView(int position, View convertView, ViewGroup parent) {		if (convertView == null) {			convertView = View.inflate(context,					R.layout.multi_picture_menu_item, null);					}		MagicImageView image = ViewHolder.get(convertView, R.id.multi_picture_select_menu_thumbmail);		TextView textView = ViewHolder.get(convertView, R.id.multi_picture_select_menu_textview);		TextView textCountView = ViewHolder.get(convertView, R.id.multi_picture_select_menu_count_textview);		View checkView = ViewHolder.get(convertView, R.id.multi_picture_select_check);		AlbumItem imageMenuItem = menuList.get(position);		textView.setText(imageMenuItem.fileFolderName);		if(imageMenuItem.bucket_id == 0){			textCountView.setVisibility(View.GONE);		}else{			textCountView.setVisibility(View.VISIBLE);			textCountView.setText(imageMenuItem.count + "å¼ ");		}		if(imageMenuItem.isChecked){			checkView.setVisibility(View.VISIBLE);		}else{			checkView.setVisibility(View.GONE);		}		image.loadLocalImage(imageMenuItem.imagepath, AlbumManager.THUMBNAIL_SIZE,				context.getResources().getDrawable(R.drawable.xx_ic_slt),null);		return convertView;	}	public int getCount() {		return menuList.size();	}	public Object getItem(int position) {		if (menuList.isEmpty()) {			return position;		}		return menuList.get(position);	}	public long getItemId(int position) {		if (menuList.isEmpty()) {			return 0;		}		return menuList.get(position).bucket_id;	}}